import React from "react";
import ReactDOM from "react-dom";

class Bill extends React.Component{
	constructor(props){
		super(props);
		this.state =({
			add_food_val:false,
			quantity:0,
			food_Seleceted_value:"",
			food_item : "",
			price :0
		})
	}

	add_food =(e) => {
		let price_val =0;
		let quan =document.getElementById('quantity').value 
		// let dosa_price = 0;
		// let briyani_price =0;
		// let shawarma_price =0;
		// let meals_price = 0;
		console.log(quan)
		if(!this.add_food_val){
			if(this.state.food_Seleceted_value === "Idly"){
				console.log("idly")
				price_val = 70*this.state.quantity
			}

			else if(this.state.food_Seleceted_value === "Dosa"){
				price_val = 50 * this.state.quantity
			}

			else if(this.state.food_Seleceted_value === "Briyani"){
				price_val = 100 * this.state.quantity
				console.log(price_val)
			}

			else if(this.state.food_Seleceted_value === "Shawarma"){
				price_val = 90 * this.state.quantity
			}

			else if(this.state.food_Seleceted_value === "Meals"){
				price_val = 75 * this.state.quantity
			}

			this.setState({
				price : this.state.price = price_val
			})

		}
		console.log(this.state.price)
		console.log(this.state.food_Seleceted_value)
		console.log(this.state.quantity)
	}

	food_items = (e) =>{
		//console.log(e.target.value)
		let food_val = e.target.value
		this.setState({
			food_Seleceted_value: food_val
		})
		//console.log(this.food_Seleceted_value)
	}

	number_quantity = (e) =>{
		console.log(e.target.value)
		this.setState ({
			quantity:e.target.value
		})
		console.log(this.quantity)
	}
	render(){
		return(


    <div  className="container">
      <br/>
      <br/>
      <div className="col">
        <form>
          <div className="form-row">
            <div className="col-lg-3">
              <div className="dropdown">
                <button className="btn btn-primary btn-block dropdown-toggle" data-toggle="dropdown" aria-expanded="false" type="button">Dropdown 
                </button>
                <div role="menu" className="dropdown-menu">
                  <a role="presentation" className="dropdown-item" href="#">First Item
                  </a>
                  <a role="presentation" className="dropdown-item" href="#">Second Item
                  </a>
                  <a role="presentation" className="dropdown-item" href="#">Third Item
                  </a>
                </div>
              </div>
            </div>
            <div className="col-lg-1">
              <input type="text" className="form-control" placeholder="something"/>
            </div>
          </div>
          <br/>
          <div className="col-lg-3">
          <label>Label
          </label>
          <input type="text" className="form-control" placeholder="hey" />
          </div>
          <div className="col">
            <br/>
            <button className="btn btn-primary" type="submit">Button
            </button>
            <br/>
          </div>
          <br/>
          <div className="form-row">
            <div className="col-lg-3">
              <div className="form-group mx-sm-1 mb-1">
                <input type="text" className="form-control"  placeholder="something"/>
              </div>
              <button type="submit" className="btn btn-primary mb-2">Confirm identity
              </button>						
            </div>
            <div className="col-lg-3">
              <div className="form-group mx-sm-1 mb-1">
                <input type="text" className="form-control"  placeholder="something" />
              </div>
              <button type="submit" className="btn btn-primary mb-2">Confirm identity
              </button>							
            </div>
          </div>
        </form>
      </div>
    </div>






			);
	}
}
export default Bill;
