import React from "react";
import ReactDOM from "react-dom";

class Bill extends React.Component{
	constructor(props){
		super(props);
		this.state =({
			add_food_val:false,
			quantity:0,
			food_Seleceted_value:"",
			food_item : "",
			 food_details:[],
			 food_obj:{
			 	// name_of_food:"",
			 	// num_of_quantity:"",
			 	// price_of_food:0,
			 },
			// food_obj.name_of_food:"",
			// food_obj.num_of_quantity:"",
			// food_obj.price_of_food:"",

			price :0
		})
	}

	add_food =(e) => {
		let price_val =0;
		let quan =document.getElementById('quantity').value 
		// let dosa_price = 0;
		// let briyani_price =0;
		// let shawarma_price =0;
		// let meals_price = 0;
		console.log(quan)

			console.log("first")
			if(this.state.food_Seleceted_value === "Idly"){
				console.log("idly")
				price_val = 70*this.state.quantity
			}

			else if(this.state.food_Seleceted_value === "Dosa"){
				price_val = 50 * this.state.quantity
			}

			else if(this.state.food_Seleceted_value === "Briyani"){
				price_val = 100 * this.state.quantity
				console.log(price_val)
			}

			else if(this.state.food_Seleceted_value === "Shawarma"){
				price_val = 90 * this.state.quantity
			}

			else if(this.state.food_Seleceted_value === "Meals"){
				price_val = 75 * this.state.quantity
			}

			this.setState({
				price : price_val,
				food_obj:this.state.food_obj = {
					name_of_food:this.state.food_Seleceted_value,
					num_of_quantity:this.state.quantity,
					price_of_food:this.state.price = price_val
				},
				food_details:this.state.food_details.concat([{name_of_food:this.state.food_Seleceted_value,num_of_quantity:this.state.quantity,price_of_food:this.state.price}])

			})


		console.log(this.state.price)
		console.log(this.state.food_Seleceted_value)
		console.log(this.state.quantity)
		console.log(this.state.food_details)
		console.log(this.state.food_obj.name_of_food)

	}

	food_items = (e) =>{
		//console.log(e.target.value)
		let food_val = e.target.value
		this.setState({
			food_Seleceted_value: food_val
		})
		console.log(this.state.food_Seleceted_value)
	}

	number_quantity = (e) =>{
		console.log(e.target.value)
		this.setState ({
			quantity:e.target.value
		})
		console.log(this.state.quantity)
	}
	render(){
		console.log(this.state.food_details)
		return(
			<div>
				<div className ="jumbotron">
						<div className="container">
							<h1 className="text-center">BILL GENERATOR</h1>
						</div>
				</div>
			 <div  className="container">
		      <br/>
		      <br/>
		      <div className="col">
		        <form>
		          <div className="form-row">
		            <div className="col-3">
								<label for="food_name">Food</label>
								<select onChange ={this.food_items} className="form-control" id="food_name">
									<option>Idly</option>
									<option>Dosa</option>
									<option>Briyani</option>
									<option>Shawarma</option>
									<option>Meals</option>
								</select>
		            </div>
		            <div className="col-1 my-4">
		              <input type="number" className="form-control" placeholder="something"/>
		            </div>
		          </div>
		          <br/>
		          <label>Label
		          </label>
		          <div className="col-3">
		          	<input type="text" className="form-control" placeholder="hey" />
		          </div>
		          <div className="col-3 text-center">
		            <br/>
		            <button className="btn btn-primary" type="submit">Button
		            </button>
		            <br/>
		          </div>
		          <br/>
		          <div className="form-row">
		            <div className="col-3">
		              <div className="form-group mx-sm-1 mb-1">
		                <input type="text" className="form-control"  placeholder="Tax"/>
		              </div>
		              <button type="submit" className="btn btn-primary mb-2">Add Tax
		              </button>						
		            </div>
		            <div className="col-3">
		              <div className="form-group mx-sm-1 mb-1">
		                <input type="text" className="form-control"  placeholder="Discount"/>
		              </div>
		              <button type="submit" className="btn btn-primary mb-2">Add Discount
		              </button>							
		            </div>
		          </div>
		        </form>
		      </div>
		    </div>
		    </div>
			);
	}
}
export default Bill;
